# --- –ö–æ–º–∞–Ω–¥–∞ /start –∏ –∫–Ω–æ–ø–∫–∏ ---
@bot.message_handler(commands=['start'])
def welcome(message):
    user_id = message.chat.id
    today = datetime.date.today()

    if user_id not in subscribed_users:
        subscribed_users[user_id] = {'last_date': today, 'notified': False}

    markup = types.InlineKeyboardMarkup()
    markup.add(
        types.InlineKeyboardButton("üíñ –û—Ç–Ω–æ—à–µ–Ω–∏—è", callback_data='love'),
        types.InlineKeyboardButton("üí∞ –î–µ–Ω—å–≥–∏", callback_data='money'),
        types.InlineKeyboardButton("üîÆ –ó–∞–≤—Ç—Ä–∞", callback_data='state')
    )
    bot.send_message(user_id, "–í—ã–±–µ—Ä–∏ —Ç–µ–º—É —Ä–∞—Å–∫–ª–∞–¥–∞ –Ω–∞ —Å–µ–≥–æ–¥–Ω—è:", reply_markup=markup)

# --- –û–±—Ä–∞–±–æ—Ç–∫–∞ –∫–Ω–æ–ø–æ–∫ ---
@bot.callback_query_handler(func=lambda call: True)
def handle_query(call):
    user_id = call.message.chat.id
    today = get_today_day()
    today_date = datetime.date.today()

    # –°–±—Ä–æ—Å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è, –µ—Å–ª–∏ –Ω–∞—Å—Ç—É–ø–∏–ª –Ω–æ–≤—ã–π –¥–µ–Ω—å
    if user_id in subscribed_users and subscribed_users[user_id]['last_date'] != today_date:
        subscribed_users[user_id]['notified'] = False
        subscribed_users[user_id]['last_date'] = today_date

    if call.data == 'love':
        text = love_readings.get(today, "–ù–µ—Ç —Ä–∞—Å–∫–ª–∞–¥–∞ –Ω–∞ —Å–µ–≥–æ–¥–Ω—è.")
    elif call.data == 'money':
        text = money_readings.get(today, "–ù–µ—Ç —Ä–∞—Å–∫–ª–∞–¥–∞ –Ω–∞ —Å–µ–≥–æ–¥–Ω—è.")
    elif call.data == 'state':
        text = state_readings.get(today, "–ù–µ—Ç –ø—Ä–æ–≥–Ω–æ–∑–∞ –Ω–∞ —Å–µ–≥–æ–¥–Ω—è.")
    else:
        bot.send_message(user_id, "–ù–µ–ø–æ–Ω—è—Ç–Ω—ã–π –≤—ã–±–æ—Ä.")
        return

    bot.send_message(user_id, text)

    # –û—Ç–ø—Ä–∞–≤–∏—Ç—å –∞–≤—Ç–æ-—Å–æ–æ–±—â–µ–Ω–∏–µ —Ç–æ–ª—å–∫–æ 1 —Ä–∞–∑ –≤ –¥–µ–Ω—å
    if user_id in subscribed_users and not subscribed_users[user_id]['notified']:
        Thread(target=lambda: delayed_offer(user_id)).start()
        subscribed_users[user_id]['notified'] = True

def delayed_offer(user_id):
    time.sleep(10)
    bot.send_photo(user_id, photo_id, caption=auto_message)

